import React, { useState } from "react";
import axios from "axios";

function App() {
  const [name, setName] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    const data = {
      name: name,
      username: username,
      password: password,
    };
    axios
      .post("/api/user", data)
      .then((response) => {
        console.log(response.data);
        // 요청이 성공적으로 처리된 경우 추가 작업을 수행할 수 있습니다.
      })
      .catch((error) => {
        console.error(error);
        // 요청이 실패한 경우 에러 처리를 수행할 수 있습니다.
      });
  };

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        이름 : <input type="text" value={name} onChange={(e) => setName(e.target.value)}></input>
        아이디 : <input type="text" value={username} onChange={(e) => setUsername(e.target.value)}></input>
        비밀번호 : <input type="password" value={password} onChange={(e) => setPassword(e.target.value)}></input>
        <button type="submit">전송</button>
      </form>
    </div>
  );
}

export default App;
