import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

const ListForm = () => {
  const navigate = useNavigate();
  const [data, setData] = useState([]);
  const [page, setPage] = useState(1); // 현재 페이지
  const [totalPage, setTotalPage] = useState([]);

  useEffect(() => {
    fetchData();
  }, [page]); // 페이지가 변경될 때마다 데이터를 가져옴

  const fetchData = async () => {
    try {
      const response = await axios.get(`/api/list?page=${page}`);
      setData(response.data.data);
      pagenation(Math.ceil(response.data.totalCount / 10));
    } catch (error) {
      console.error(error);
    }
  };

  pagenation = (totalPage) => {
    setTotalPage(Math.ceil(response.data.totalCount / 10));
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>게시물번호</th>
            <th>제목</th>
            <th>작성자</th>
          </tr>
        </thead>
        <tbody>
          {data.map((d) => (
            <tr key={d.boardno}>
              <td>{d.boardno}</td>
              <td>
                <Link to={`/read/${d.boardno}`}>{d.title}</Link>
              </td>
              <td>{d.writer}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        <ul>
          <li></li>
          <li></li>
        </ul>
      </div>
      <button onClick={() => navigate("/write")}>글 작성</button>
    </div>
  );
};

export default ListForm;
