import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

const ListForm = () => {
  const navigate = useNavigate();
  const [data, setData] = useState([]);
  const [page, setPage] = useState(1); // 현재 페이지
  const [limit, setLimit] = useState(10); // 페이지 당 아이템 수
  const [totalPages, setTotalPages] = useState(0); // 전체 페이지 수

  useEffect(() => {
    fetchData();
  }, [page]); // 페이지가 변경될 때마다 데이터를 가져옴

  const fetchData = async () => {
    try {
      const response = await axios.get(`/api/list?page=${page}&limit=${limit}`);
      setData(response.data);
      setTotalPages(Math.ceil(response.data.length / limit));
    } catch (error) {
      console.error(error);
    }
  };

  const handlePrevPage = () => {
    if (page > 1) {
      setPage(page - 1);
    }
  };

  const handleNextPage = () => {
    if (page < totalPages) {
      setPage(page + 1);
    }
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>게시물번호</th>
            <th>제목</th>
            <th>작성자</th>
          </tr>
        </thead>
        <tbody>
          {data.map((d) => (
            <tr key={d.boardno}>
              <td>{d.boardno}</td>
              <td>
                <Link to={`/read/${d.boardno}`}>{d.title}</Link>
              </td>
              <td>{d.writer}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        <button onClick={handlePrevPage}>이전</button>
        {Array.from({ length: totalPages }, (_, index) => (
          <button key={index} onClick={() => setPage(index + 1)} disabled={page === index + 1}>
            {index + 1}
          </button>
        ))}
        <button onClick={handleNextPage}>다음</button>
      </div>
      <button onClick={() => navigate("/write")}>글 작성</button>
    </div>
  );
};

export default ListForm;
