import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const LoginForm = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault(); // 기본 동작 중단

    const data = {
      username: username,
      password: password,
    };
    axios
      .post("/api/login", data)
      .then((response) => {
        console.log(response.data);
        navigate("/");
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        아이디 : <input type="text" value={username} onChange={(e) => setUsername(e.target.value)}></input>
        비밀번호 : <input type="password" value={password} onChange={(e) => setPassword(e.target.value)}></input>
        <button type="submit">로그인</button>
      </form>
      <button onClick={() => navigate("/join")}>회원가입 페이지로 이동</button>
    </div>
  );
};

export default LoginForm;
