import axios from "axios";
import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";

const ReadForm = () => {
  const navigate = useNavigate();
  const [board, setBaord] = useState([]);
  const [readComment, setReadComment] = useState([]);
  const [comment, setComment] = useState([]);
  const { boardno } = useParams();

  useEffect(() => {
    loadBoard(boardno);
  }, [boardno]);

  useEffect(() => {
    loadComment(boardno);
  }, [boardno]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = {
      comment: comment,
      token: localStorage.getItem("token"),
      boardno: baord[0].boardno,
    };
    axios
      .post("/api/commentWrite", formData)
      .then((response) => {
        // 성공적으로 처리된 경우
        window.location.reload();
      })
      .catch((error) => {
        // 오류가 발생한 경우
        alert(error.response.data.error);
      });
  };

  const loadBoard = async (boardno) => {
    try {
      const response = await axios.get("/api/read", {
        params: {
          boardno: boardno,
        },
      });
      setBaord(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const loadComment = async (boardno) => {
    try {
      const response = await axios.get("/api/commentRead", {
        params: {
          boardno: boardno,
        },
      });
      setReadComment(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      {board.length > 0 ? (
        <>
          <div>번호: {board[0].boardno}</div>
          <div>작성자: {board[0].writer}</div>
          <div>제목: {board[0].title}</div>
          <div>내용: {board[0].content}</div>
          <form onSubmit={handleSubmit}>
            <input value={comment} onChange={(e) => setComment(e.target.value)} placeholder="댓글을 작성해주세요"></input>
            <button type="submit">댓글작성</button>
          </form>
        </>
      ) : (
        <div>데이터가 없습니다.</div>
      )}

      {readComment.map((c) => (
        <div key={c.commentno}>
          <div>
            {c.comment} , {c.writer}, {c.commentno}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ReadForm;
