import axios from "axios";
import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";

const ReadForm = () => {
  const navigate = useNavigate();
  const [data, setData] = useState([]);
  const [comment, setComment] = useState([]);
  const { boardno } = useParams();

  useEffect(() => {
    fetchData(boardno);
  }, [boardno]);

  const handleSubmit = (e, boardno) => {
    e.preventDefault();
    const formData = {
      comment: comment,
      token: localStorage.getItem("token"),
      boardno: boardno,
    };

    axios
      .post("/api/commentWrite", formData)
      .then((response) => {
        // 성공적으로 처리된 경우
      })
      .catch((error) => {
        // 오류가 발생한 경우
        alert(error.response.data.error);
      });
  };

  const fetchData = async (boardno) => {
    try {
      const response = await axios.get("/api/read", {
        params: {
          boardno: boardno,
        },
      });
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      {data.length > 0 ? (
        <>
          <div>번호: {data[0].boardno}</div>
          <div>작성자: {data[0].writer}</div>
          <div>제목: {data[0].title}</div>
          <div>내용: {data[0].content}</div>
        </>
      ) : (
        <div>데이터가 없습니다.</div>
      )}
      <form onSubmit={(e) => handleSubmit(e, data[0].boardno)}>
        <input value={comment} onChange={(e) => setComment(e.target.value)} placeholder="댓글을 작성해주세요"></input>
        <button type="submit">댓글작성</button>
      </form>
    </div>
  );
};

export default ReadForm;
