import axios from "axios";
import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";

const ReadForm = () => {
  const navigate = useNavigate();
  const { boardno } = useParams();
  const [data, setData] = useState([]);
  const [comment, setComment] = useState([]);

  useEffect(() => {
    fetchData(boardno);
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = {
      comment: comment,
      token: localStorage.getItem("token"),
    };
    axios
      .post("/api/commentWrite", data)
      .then((response) => {})
      .catch((error) => {
        alert(error.response.data.error);
      });
  };

  const fetchData = async (boardno) => {
    try {
      const response = await axios.get("/api/read", {
        params: {
          boardno: boardno,
        },
      });
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <div>번호 : {data[0].boardno}</div>
      <div>작성자 : {data[0].writer}</div>
      <div>제목 : {data[0].title}</div>
      <div>내용 : {data[0].content}</div>
      <form onSubmit={handleSubmit(data[0].boardno)}>
        <input value={comment} onChange={(e) => setComment(e.target.value)} placeholder="제목을 작성해주세요"></input>
        <button>댓글작성</button>
      </form>
    </div>
  );
};

export default ReadForm;
