import axios from "axios";
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const ReadForm = () => {
  const { boardno } = useParams();
  const [data, setData] = useState([]);
  const [comment, setComment] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault(); // 기본 동작 중단
  };

  useEffect(() => {
    console.log(boardno);
    fetchData(boardno);
  }, []);

  const fetchData = async (boardno) => {
    try {
      const response = await axios.get("/api/read", {
        params: {
          boardno: boardno,
        },
      });
      console.log(response.data); // 데이터 확인용
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <div>번호 : {data[0].boardno}</div>
      <div>작성자 : {data[0].writer}</div>
      <div>제목 : {data[0].title}</div>
      <div>내용 : {data[0].content}</div>
      <form onSubmit={handleSubmit}>
        <input value={comment} onChange={(e) => setComment(e.target.value)} placeholder="제목을 작성해주세요"></input>
        <button>댓글작성</button>
      </form>
    </div>
  );
};

export default ReadForm;
