import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const JoinForm = () => {
  const navigate = useNavigate();

  const [name, setName] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    const data = {
      name: name,
      username: username,
      password: password,
    };
    axios
      .post("/api/user", data)
      .then((response) => {
        console.log(response.data);
        navigate("/login");
      })
      .catch((error) => {
        console.error(error);
      });
  };
  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        이름 : <input type="text" value={name} onChange={(e) => setName(e.target.value)}></input>
        아이디 : <input type="text" value={username} onChange={(e) => setUsername(e.target.value)}></input>
        비밀번호 : <input type="password" value={password} onChange={(e) => setPassword(e.target.value)}></input>
        <button type="submit">회원가입</button>
      </form>
    </div>
  );
};

export default JoinForm;
